@-moz-document regexp("^https?://www\.zuora\.com.*github\.com.*") {
  /* Styles the payment form iframe */
  body, label {
    background: transparent !important;
    color: #bebebe !important;
  }
  input, select {
    background: #181818 !important;
    color: #bebebe !important;
    border-color: #343434 !important;
  }
  select {
    background: #181818 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAUCAQAAAAua3X8AAAARklEQVR4AWNABWcazjQg8zGl/wNhA15piBL80gglmNKYSjClMZUgpDGVwKXxKBl4QNiRBLxJIKAIBzXhyCIc3YQTDMEkBwBplPJRxEMa4wAAAABJRU5ErkJggg==") no-repeat right 8px center !important;
    background-size: 8px 10px !important;
  }
  input:focus, select:focus {
    border-color: /*[[base-color]]*/ !important;
    box-shadow: inset 0 1px 2px rgba(0, 0, 0, .075), 0 0 2px /*[[base-color]]*/ !important;
    outline-color: /*[[base-color]]*/ !important;
  }
  .btn-submit {
    background-color: #373;
    background-image: none;
    border-color: rgba(220, 220, 220, .15);
  }
  .btn-submit:hover {
    background-color: #484;
    background-image: none;
  }
  .popup, #z_hppm_iframe {
    background-color: var(--ghd-bg-color);
  }
  /* fix top white line */
  .whitespace {
    margin: -1px !important;
  }
}
@-moz-document domain("render.githubusercontent.com"), domain("notebooks.githubusercontent.com"), domain("viewscreen.githubusercontent.com") {
  :root {
    --jp-content-font-color1: #ccc;
    --jp-content-font-color2: #bbb;
    --jp-content-font-color3: #aaa;
    --jp-content-font-color4: #999;
  }
  /* generated render.githubusercontent.com rules */
  body, .render-shell {
    background: var(--ghd-code-background) !important;
  }
  .render-shell .mermaid {
    background: var(--color-canvas-default) !important;
  }
  .render-shell img.math {
    filter: invert(100%) hue-rotate(180deg) !important;
  }
  .render-shell .output_area .output_png img {
    filter: invert(90.5%) hue-rotate(180deg) saturate(200%) brightness(85%) !important;
  }
  .render-shell .output_area .output_png:hover img {
    filter: none !important;
  }
  .render-shell .added-frame, .render-shell .deleted-frame {
    box-sizing: border-box !important;
  }
  .render-shell .swipe-frame .deleted-frame {
    margin-left: 2px !important;
  }
  .render-shell:not([data-document-path]) img, .render-shell canvas {
    border: none !important;
    background-image: url("data:image/gif;base64,R0lGODlhCgAKAIAAABoaGgAAACwAAAAACgAKAAACEYQdmYcaDNxjEspKndVZbc8UADs=") !important;
  }
  /* https://github.com/StylishThemes/GitHub-Dark/issues/1209 */
  .render-shell:not([data-document-path]) img:not([src$=".svg" i]):not(.leaflet-tile):not(.leaflet-marker-icon) {
    width: 100% !important;
    height: 100% !important;
  }
  img.leaflet-marker-icon, img.leaflet-marker-icon:hover {
    background: none !important;
  }
  .render-shell:not([data-document-path]) img:not(.leaflet-tile):not(.leaflet-marker-icon):hover,
  .render-shell:not([data-document-path]) canvas:hover {
    background-image: url("data:image/gif;base64,R0lGODlhCgAKAIAAAOXl5f///yH5BAAAAAAALAAAAAAKAAoAAAIRhB2ZhxoM3GMSykqd1VltzxQAOw==") !important;
  }
}
