  }
  .old-hero a.-link, .fc-black-900, .fc-black, .h\:fc-black:hover,
  .f\:fc-black:focus {
    color: #eee !important;
  }
  .old-hero._orange {
    color: #eee;
    background-color: /*[[base-color]]*/;
    background-image: none;
  }
  /* hack around to prevent pagination hover issues on stackexchange */
  a[href]:hover {
    color: rgba(79, 140, 201, .8);
    color: rgba(/*[[base-color-rgb]]*/, .8);
  }
  .s-pagination--item.is-selected {
    background-color: rgba(79, 140, 201, .8);
    background-color: rgba(/*[[base-color-rgb]]*/, .8);
  }
  .s-pagination--item {
    border-color: #555;
    color: #ccc;
  }
  .s-pagination--item:hover {
    background-color: rgba(79, 140, 201, .8) !important;
    background-color: rgba(/*[[base-color-rgb]]*/, .8) !important;
    border-color: #555;
    color: #eee !important;
  }
  /* end hack */
  html:not(#X) #tabs a:not(.youarehere), html:not(#X) .tabs a:not([href]) {
    color: #aaa !important;
    border: 1px solid transparent !important;
  }
  #tabs a, .tabs a {
    background: #222;
  }
  html:not(#X) #tabs a.youarehere, html:not(#X) .tabs a:not([href]).youarehere,
  html:not(#X) #tabs:hover a.youarehere,
  html:not(#X) .tabs:hover a.youarehere:not([id*="category-"]) {
    border-bottom: 1px solid transparent !important;
  }
  a.button:hover, #questions .post-menu .button.btn:hover, .star-off {
    filter: brightness(90%) !important;
  }
  .s-btn__outlined:not(.grid--cell):not(.ws-nowrap):not(.s-btn__muted):hover {
    filter: brightness(90%) !important;
    color: #ccc !important;
  }
  .user-tabs-nav #tabs a.youarehere::before,
  .user-tabs-nav .tabs a.youarehere::before,
  .newnav .tabs-list-container .tabs-list .intellitab a.youarehere::before,
  #tabs a.youarehere::before, .tabs a.youarehere::before,
  html:not(#X) .p-bg-purple:not(.teams-slope),
  input[type=submit][value="search"], html:not(#X) .h\:bg-orange-500:hover,
  html:not(#X) .wz-progress.wz-progress--active {
