  }
  #pagehistory li {
    border-color: var(--gray-2) !important;
  }
  .ns-talk .mw-body-content dl, .ns-talk .mw-body-content dl dl dl,
  .ns-talk .mw-body-content dl dl dl dl dl,
  .ns-talk .mw-body-content dl dl dl dl dl dl dl,
  .ns-talk .mw-body-content dl dl dl dl dl dl dl dl dl,
  .ns-talk .mw-body-content dl dl dl dl dl dl dl dl dl dl dl,
  .ns-talk .mw-body-content dl dl dl dl dl dl dl dl dl dl dl dl dl {
    background: inherit;
  }
  a.image img[src*="Tango"] {
    background: inherit !important;
  }
  .ext-related-articles-card-list .ext-related-articles-card-thumb {
    background-color: var(--gray-28) !important;
  }
  .ext-related-articles-card-list .ext-related-articles-card {
    background-color: var(--gray-28);
  }
  .ext-related-articles-card-list .ext-related-articles-card-thumb,
  .uls-icon-close {
    filter: invert(1) grayscale(1) brightness(140%) contrast(105%);
  }
  .ext-related-articles-card-list h3::after {
    background-image: linear-gradient(to right, rgba(255, 255, 255, 0), var(--gray-28) 50%);
  }
  .ns-talk .mw-body-content dl {
    border-top: 0;
    border-left: 0;
  }
  .mw-rcfilters-ui-filterTagMultiselectWidget-hideshowButton .oo-ui-labelElement-label {
    border: 0 !important;
  }
  #content a:active, #mw-navigation li:not(.new) a:active,
  #mw-panel li:not(.new) a:active, #column-one li:not(.new) a:active,
  #footer a:active, #content a:focus, #mw-navigation li:not(.new) a:focus,
  #mw-panel li:not(.new) a:focus, #column-one li:not(.new) a:focus,
  #footer a:focus, #content a:active:hover,
  #mw-navigation li:not(.new) a:active:hover,
  #mw-panel li:not(.new) a:active:hover, #column-one li:not(.new) a:active:hover,
  #footer a:active:hover, #content a:focus:hover,
  #mw-navigation li:not(.new) a:focus:hover,
  #mw-panel li:not(.new) a:focus:hover, #column-one li:not(.new) a:focus:hover,
  #footer a:focus:hover, .uls-language-block a:hover {
    color: var(--base-color) !important;
  }
  /* hack around sidebar bottom border brightness */
  .portal h3, .vector-menu-portal h3 {
    background-image: linear-gradient(to top, transparent, var(--gray-c));
    background-size: auto 1px;
  }
  nav.vector-menu-tabs li.selected {
    border-color: var(--gray-5);
    background-color: inherit;
  }
}
@-moz-document domain("wiki.mozilla.org") {
  .mw-wiki-logo {
    filter: invert(100%) hue-rotate(180deg) brightness(80%);
  }
  img[alt="Sumo-banner.png"] {
    filter: invert(100%) hue-rotate(180deg) brightness(90%);
  }
  div#simpleSearch {
    border: solid 1px var(--gray-5);
    color: var(--gray-c);
    background-color: var(--gray-18);
  }
  input[type="search"]#searchInput:focus, #simpleSearch:hover #searchInput:focus {
    -webkit-box-shadow: none;
            box-shadow: none;
    border-color: var(--base-color) !important;
    -webkit-appearance: none;
            appearance: none;
  }
  input#searchButton {
    border-left: 1px solid var(--gray-5) !important;
    border-top: 0 !important;
    border-bottom: 0 !important;
  }
  div.vectorTabs ul li {
    background-color: transparent !important;
  }
  div.vectorTabs li:not(.selected) {
    border-bottom: 0 !important;
  }
  #mw-panel .portal:not(#p-navigation) .body {
    background-image: none;
  }
  div#mw-panel div.portal {
    background-image: linear-gradient(to top, transparent, var(--gray-c));
    background-size: auto 1px;
  }
  :not(.color_swatch) > div[style*="background-color: lightcyan"] {
    background-color: var(--blue-2) !important;
  }
  div[style*="border: 0.1em solid darkcyan"] {
    border-color: var(--blue-4) !important;
  }
  :not(.color_swatch) > div[style*="background-color: lightyellow"] {
    background-color: var(--brown-1) !important;
  }
  div[style*="border: 0.1em solid olive"] {
    border-color: var(--brown-2) !important;
  }
  div[style*="background-color: lightyellow"] div h2 {
    background-color: var(--brown-1) !important;
  }
  div[style*="ackground-color: palegreen"] {
    background-color: var(--green-4) !important;
  }
  div[style*="background-color: #FFEA00; color: black"] {
    color: var(--gray-1) !important;
  }
  #wpTextbox1 {
    color: var(--gray-b);
  }
  #pagehistory li {
    border-color: var(--gray-5);
  }
}

