	.footer__card .footer__text, .footer__text
	{
		color: var(--dimmer-text) !important;
	}
	.footer__card__title:hover
	{
		color: var(--main-color) !important;
	}

	/*Page number*/
	.result__pagenum
	{
		color: var(--main-text) !important;
	}

	/*Privacy simplified*/
	.header__label, .header__link, .header__clickable
	{
		color: var(--main-text) !important;
	}
	.header__clickable:hover, .header__clickable:focus
	{
		color: var(--main-color) !important;
	}
	.showcase .showcase__dropdown-top .showcase__dropdown__list:hover
	{
		background-color: var(--hover-background) !important;
	}
	.showcase .showcase__dropdown-bottom
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
}
@-moz-document url-prefix("https://duckduckgo.com/html") {
	/*Issues in html mode*/
	.search__input, .search__input--adv
	{
		color: var(--dimmer-text) !important;
	}
	.header.header--html
	{
		border-color: var(--hover-background) !important;
	}
	.btn--alt:hover
	{
		color: var(--main-color) !important;
	}
	.logo_homepage
	{
		background-image: var(--logo) !important;
	}
	.header__logo-wrap
	{
		background-image: var(--biglogo) !important;
	}
	.search, .search--adv
	{
		background-color: var(--second-background) !important;
		border-color: var(--second-background) !important;
	}
	.zci-wrapper
	{
		background-color: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.zci
	{
		color: var(--dimmer-text) !important;
	}
}
@-moz-document url-prefix("https://duckduckgo.com/lite/") {
	/*logo*/
	.header
	{
		color: var(--main-color) !important;
	}

	/*Search bar*/
	.query
	{
		background-color: var(--second-background) !important;
		border-color: var(--second-background) !important;
		color: var(--main-text) !important;
	}
	.query:hover, .query:focus, .query:active
	{
		border-color: var(--main-color) !important;
	}
	.submit
	{
		background-color: var(--second-background) !important;
		border: 1px solid var(--hover-background) !important;
		box-shadow: var(--shadow) !important;
	}
	.submit:hover
	{
		background-color: var(--hover-background) !important;
		color: var(--main-color) !important;
	}

	/*Searches*/
	tr, .navbutton
	{
		color: var(--dimmer-text) !important;
	}
	.result-link
	{
		color: var(--main-text) !important;
	}
	.link-text, .navbutton:hover
	{
		color: var(--main-color) !important;
	}
}

