		}
		else if colorRGB == "Solarized"
		{
			--red: #dc322f;
			--yellow: #b58900;
			--orange: #cb4b16;
			--green: #859900;
			--cyan: #2aa198;
			--blue: #268bd2;
			--purple: #6c71c4;
			--pink: #d33682;
			--gray: #93A1A1;
		}
		else if colorRGB == "Gruvbox_Dark"
		{
			--red: #cc241d;
			--yellow: #d79921;
			--orange: #d65d0e;
			--green: #98971a;
			--cyan: #689d6a;
			--blue: #458588;
			--purple: #b16286;
			--pink: #b16286;
			--gray: #a89984;
		}
		else if colorRGB == "Gruvbox_Dark_Intense"
		{
			--red: #fb4934;
			--yellow: #fabd2f;
			--orange: #fe8019;
			--green: #b8bb26;
			--cyan: #8ec07c;
			--blue: #83a598;
			--purple: #d3869b;
			--pink: #d3869b;
			--gray: #928374;
		}
		else if colorRGB == "Gruvbox_Light"
		{
			--red: #cc241d;
			--yellow: #d79921;
			--orange: #d65d0e;
			--green: #98971a;
			--cyan: #689d6a;
			--blue: #458588;
			--purple: #b16286;
			--pink: #b16286;
			--gray: #7c6f64;
		}
		else if colorRGB == "Gruvbox_Light_Intense"
		{
			--red: #9d0006;
			--yellow: #b57614;
			--orange: #af3a03;
			--green: #79740e;
			--cyan: #427b58;
			--blue: #076678;
			--purple: #8f3f71;
			--pink: #8f3f71;
			--gray: #928374;
		}
		else if colorRGB == "Custom"
		{
			--red: colorRed;
			--yellow: colorYellow;
			--orange: colorOrange;
			--green: colorGreen;
			--cyan: colorCyan;
			--blue: colorBlue;
			--purple: colorPurple;
			--pink: colorPink;
			--gray: colorGray;
		}
	}
}

@-moz-document regexp("https:\/\/myanimelist.net\/(?!(animelist|mangalist|ownlist\/style\/theme)).*") {

	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
