/* ==UserStyle==
@name           Material Palenight Stylus
@namespace      USO Archive
@author         Brett M
@description    `Stylus theme based on the <a href="https://marketplace.visualstudio.com/items?itemName=whizkydee.material-palenight-theme">Material Palenight</a> vscode theme`
@version        20190111.18.19
@license        CC0-1.0
@preprocessor   uso
@advanced text code-font "Code Font" "Menlo"
@advanced text code-font-size "Code Font Size" "16px"
@advanced text code-font-weight "Code Font Weight" "500"
@advanced text ui-font "UI Font" "Noto Sans"
@advanced text ui-font-size "UI Font Size" "12px"
@advanced text ui-font-weight "UI Font Weight" "500"
@advanced color cursor-color "Cursor Color" #C792EA

==/UserStyle== */
@-moz-document regexp('chrome-extension://.*'), regexp('moz-extension://.*') {
  :root {
    --bg0: #232635;
    --bg1: #292d3e;
    --bg2: #2b2f3f;
    --bg3: #313545;
    --bg4: #353949;
    --fg0: #bfc7d5;
    --fg1: #eeffff;
    --blue1: #82aaff;
    --blue2: #78ccf0;
    --orange: #f78c6c;
    --pink: #c792ea;
    --text: #a6accd;
    --comment: #676e95;
    --green: #c3e88d;
    --red: #ff5370;
  }

  /* CODE AREA */
  .CodeMirror {
    background: var(--bg1);
    font-family: '/*[[code-font]]*/', Consolas, 'Liberation Mono', Menlo, Courier, monospace;
    font-size: /*[[code-font-size]]*/;
    font-weight: /*[[code-font-weight]]*/;
  }

  .CodeMirror-activeline-background {
    background: var(--bg2);
  }

  .cm-s-default .cm-qualifier,
  .cm-s-default .cm-number {
    color: var(--orange);
  }

  .cm-s-default .cm-builtin {
    color: var(--blue2);
  }

  .cm-s-default .cm-comment {
    color: var(--comment);
  }

  .CodeMirror-wrap pre,
  .cm-s-default .cm-attribute {
    color: var(--text);
  }

  .cm-s-default .cm-variable-2,
  .cm-s-default .cm-tag {
    color: var(--red);
  }

  .cm-s-default .cm-variable-3,
  .cm-s-default .cm-type {
    color: var(--orange);
  }

  .cm-s-default .cm-atom {
    color: var(--blue1);
  }

  .cm-s-default .cm-string {
    color: var(--green);
  }

  .CodeMirror-gutters {
    background: var(--bg1);
    border-color: var(--bg1);
  }

  .CodeMirror,
  .CodeMirror-focused {
    border: none !important;
    outline-width: 0;
  }

  .cm-s-default .cm-keyword,
  .cm-s-default .cm-def {
    color: var(--pink);
  }

  .resize-grip {
    border-color: var(--bg3);
  }

  .resize-grip::after {
    border-color: var(--bg3);
  }

  .colorview-swatch::after {
    border-color: #000000;
  }

  .CodeMirror-cursor {
    border-left: 1px solid /*[[cursor-color]]*/ !important;
  }

  .cm-fat-cursor .CodeMirror-cursor {
    background: /*[[cursor-color]]*/ !important;
  }

  div.CodeMirror-selected {
    background: var(--bg3) !important;
  }

  .section {
    background: var(--bg1);
  }

  /* GLOBAL */
  body,
  html {
    background: var(--bg0);
    font-family: '/*[[ui-font]]*/', 'Roboto', 'Open Sans', Arial, sans-serif;
    font-weight: /*[[ui-font-weight]]*/;
    font-size: /*[[ui-font-size]]*/;
  }

  #sections > div > label,
  #sections h2 {
    color: var(--fg);
    font-family: '/*[[ui-font]]*/', 'Roboto', 'Open Sans', Arial, sans-serif;
    font-weight: 400;
  }

  #sections h2 {
    margin-bottom: 10px;
  }

  #sections > div {
    margin-top: 0;
    padding-top: 5px;
  }

  /* HEADER */
  #header {
    border-right: 1px solid transparent;
    overflow-x: hidden;
    overflow-y: auto;
    background: var(--bg1);
    box-shadow: 0 0 15px rgba(0, 0, 0, .6);
  }

  #header h1,
  #header h2,
  #header h3,
  #header p,
  #header span,
  #header a,
  .applies-to li {
    color: var(--fg1);
    font-weight: 400;
    font-family: '/*[[ui-font]]*/', 'Roboto', 'Open Sans', Arial, sans-serif;
  }

  #header label {
    color: var(--fg1);
  }

  #header select,
  input[type='number'],
  #header input[type='checkbox']:not(.slider),
  #header input:not([type]) {
    border: 1px solid var(--bg0);
    background: var(--bg0);
    color: var(--fg1) !important;
    font-family: '/*[[ui-font]]*/', 'Roboto', 'Open Sans', Arial, sans-serif;
  }

  #header .filter-selection label {
    margin-bottom: 10px;
    width: 100%;
    height: 40px;
  }

  #header .select-resizer {
    height: 40px;
    width: 100%;
  }

  #header select {
    padding: 10px;
    height: 40px;
    width: 100% !important;
  }

  #header .select-resizer select {
    margin-left: 5px;
  }

  #header input[type='checkbox']:not(.slider):hover {
    background: var(--bg3);
    border: 1px solid var(--bg3);
  }

  .svg-icon.select-arrow {
    fill: var(--fg1);
    top: 50%;
    right: 10px;
    transform: translateY(-50%);
  }

  #header button {
    background: var(--bg0);
    border-color: var(--bg0);
    color: var(--fg1);
    border-radius: 5px;
    cursor: pointer;
    padding: 8px;
    font-family: '/*[[ui-font]]*/', 'Roboto', 'Open Sans', Arial, sans-serif;
  }

  #header button:hover {
    background: var(--bg3);
    border-color: var(--bg3);
  }

  .svg-icon,
  .svg-icon.info:hover,
  .svg-icon.settings:hover {
    fill: #aaaaaa;
  }

  #filters label:hover,
  #filters .filter-selection:hover {
    background: transparent;
  }

  /* MANAGE */
  .style-name a,
  .style-edit-link,
  .newUI .target {
    color: var(--fg1);
  }

  .newUI .entry.enabled .style-name:hover .style-name-link {
    color: var(--blue2);
  }

  .newUI .entry.odd {
    background: var(--bg1);
  }

  .newUI .entry .style-name:hover::before {
    background: linear-gradient(
      to right,
      rgba(130, 170, 255, .05),
      rgba(130, 170, 255, .1) 50%,
      transparent
    );
  }

  #search,
  #manage\.newUI\.sort {
    background: var(--bg0);
    border-color: var(--bg0);
    padding: 15px;
    color: var(--fg1);
  }

  /* APPLIES TO */
  .edit-actions button {
    background: var(--bg0);
    border-color: var(--bg0);
    color: var(--fg1);
    border-radius: 5px;
    cursor: pointer;
    margin-left: 5px;
    font-family: '/*[[ui-font]]*/', 'Roboto', 'Open Sans', Arial, sans-serif;
  }

  .edit-actions button:hover {
    background: var(--bg3);
    border-color: var(--bg3);
  }

  .applies-to select,
  .applies-to li .applies-value {
    background: var(--bg0);
    border-color: var(--bg0);
    cursor: pointer;
    border-radius: 5px;
    color: var(--fg1);
    font-family: '/*[[ui-font]]*/', 'Roboto', 'Open Sans', Arial, sans-serif;
  }

  .applies-to label {
    color: var(--fg1);
    font-family: '/*[[ui-font]]*/', 'Roboto', 'Open Sans', Arial, sans-serif;
  }

  .applies-to li > :not(button):not(.select-resizer) {
    color: var(--fg1);
    background: var(--bg0);
    border-color: var(--bg0);
    border-radius: 5px;
  }

  #sections > div:nth-child(2) {
    background: var(--bg1);
  }

  #sections > div:not(:first-of-type) {
    background: var(--bg1);
    border-color: transparent;
  }

  #sections > div > button:not(:first-of-type),
  .remove-section {
    background: var(--bg0);
    color: var(--fg1);
    border-color: var(--bg0);
    border-radius: 5px;
    cursor: pointer;
  }

  #sections > div > button:not(:first-of-type):hover,
  .remove-section:hover {
    background: var(--bg3) !important;
    border-color: var(--bg3) !important;
  }

  /* POP UP */
  .style-name {
    color: #eeeeee;
  }

  #stylus-popup {
    background: var(--bg1);
  }

  #installed .style-name .checker,
  #installed .style-name .svg-icon.checked,
  input[type='checkbox']:not(.slider):checked + .svg-icon.checked {
    background: var(--bg3);
    border-color: var(--bg3);
    fill: var(--fg1) !important;
  }

  #stylus-popup .actions {
    color: var(--fg1) !important;
  }

  #stylus-popup .actions a {
    color: var(--fg1) !important;
  }

  #stylus-popup input[type='checkbox']:not(.slider) {
    background: var(--bg3);
    border-color: var(--bg3);
  }

  #stylus-popup input[type='checkbox']:not(.slider):hover {
    background: var(--bg4);
    border-color: var(--bg4);
  }

  #popup-options button {
    background: var(--bg0);
    border-color: var(--bg0);
    color: var(--fg1);
    padding: 5px;
    border-radius: 5px;
    cursor: pointer;
  }

  #popup-options button:hover {
    background: var(--bg3);
    border-color: var(--bg3);
    color: var(--fg1);
    border-radius: 5px;
    cursor: pointer;
  }

  #stylus-popup .search-results-nav,
  #stylus-popup .search-results-nav button {
    color: var(--fg1) !important;
  }

  #stylus-popup .search-result {
    background: var(--bg1);
    border-color: var(--bg1);
    box-shadow: none;
  }

  .search-result-title {
    color: var(--fg1);
    text-decoration: none;
  }

  .search-result:hover .search-result-title {
    color: var(--fg1);
  }

  .search-result-description {
    color: var(--fg0);
  }

  .search-result-meta {
    background: var(--bg1) !important;
    color: var(--fg1) !important;
